import React, { useEffect, FC, useState } from "react";
import styled from "styled-components";
import axios, { AxiosResponse } from "axios";
import { SummaryCovidResponse, SpecificInfo, Country } from "./types";
import "./App.css";

const TitlePanel = styled.div`
  width: 80vw;
  margin: 30px auto;
  border-radius: 4px;
  border: 1px solid black;
  padding: 1rem;
  background-color: rgba(0, 0, 0, 0.76);
`;

const Title = styled.p`
  font-size: 30px;
  font-weight: bold;
  color: white;
`;

const MainInfoWrapper = styled.div`
  background-color: rgba(0, 0, 0, 0.9);
  width: 90%;
  margin: 30px auto;
  border-radius: 4px;
  border: 1px solid black;
  display: flex;
  justify-content: space-around;
`;

const SpecificInfoWrapper = styled.div``;

const SpecificInfoTitle = styled.p`
  font-size: 16px;
  color: gray;
`;
const SpecificInfoIncreaseNum = styled.p`
  font-size: 24px;
  color: red;
`;

const SpecificInfoTotalNum = styled.p`
  font-size: 16px;
  color: white;
`;

// api
function getSummaryCovidData(): Promise<AxiosResponse<SummaryCovidResponse>> {
  const url = "https://api.covid19api.com/summary";
  return axios.get(url);
}

const App: FC = () => {
  async function initData() {
    const { data } = await getSummaryCovidData();
    /** TODO:
     * 로컬 스토리지에 저장해둔 글로벌 정보가 있으면 해당 정보가 제일 먼저 보이도록,
     * 만약 없으면 Global로 가는 것
     *  */
    setGlobalInfo(data.Global);
    setCountryInfo(data.Countries);
  }

  const [countryInfo, setCountryInfo] = useState<Array<Country>>();
  const [globalInfo, setGlobalInfo] = useState<SpecificInfo>({
    NewConfirmed: 0,
    NewDeaths: 0,
    NewRecovered: 0,
    TotalConfirmed: 0,
    TotalDeaths: 0,
    TotalRecovered: 0,
  });

  useEffect(() => {
    initData();
  }, []);

  return (
    <div className="App">
      <TitlePanel>
        <Title> 코로나바이러스감염증-19</Title>
        <MainInfoWrapper>
          <SpecificInfoWrapper>
            <SpecificInfoTitle> 확진환자 </SpecificInfoTitle>
            <SpecificInfoIncreaseNum>
              {globalInfo.NewConfirmed}
            </SpecificInfoIncreaseNum>
            <SpecificInfoTotalNum>
              {globalInfo.TotalConfirmed}
            </SpecificInfoTotalNum>
          </SpecificInfoWrapper>
          <SpecificInfoWrapper>
            <SpecificInfoTitle> 사망자 </SpecificInfoTitle>
            <SpecificInfoIncreaseNum>
              {globalInfo.NewDeaths}
            </SpecificInfoIncreaseNum>
            <SpecificInfoTotalNum>
              {globalInfo.TotalDeaths}
            </SpecificInfoTotalNum>
          </SpecificInfoWrapper>
          <SpecificInfoWrapper>
            <SpecificInfoTitle> 격리해제 </SpecificInfoTitle>
            <SpecificInfoIncreaseNum>
              {globalInfo.NewRecovered}
            </SpecificInfoIncreaseNum>
            <SpecificInfoTotalNum>
              {globalInfo.TotalRecovered}
            </SpecificInfoTotalNum>
          </SpecificInfoWrapper>
          {/* <SpecificInfoWrapper>
            <SpecificInfoTitle> 치명률 </SpecificInfoTitle>
            <SpecificInfoIncreaseNum>24</SpecificInfoIncreaseNum>
            <SpecificInfoTotalNum>3000</SpecificInfoTotalNum>
          </SpecificInfoWrapper> */}
        </MainInfoWrapper>
      </TitlePanel>
    </div>
  );
};

export default App;
